        /// <summary>
        /// x-0-9 svg初始化
        /// </summary>
        private void InitMapNumToDrawingImage(int key, string textGeometry, string outLineGeometry)
        {
            try
            {
                // Create DrawingGroup
                DrawingGroup rootDrawingGroup = new DrawingGroup
                {
                    ClipGeometry = Geometry.Parse("M0,0 V18 H13 V0 H0 Z")
                };

                // Create nested DrawingGroup with Opacity
                DrawingGroup nestedDrawingGroup = new DrawingGroup
                {
                    Opacity = 1
                };

                // Create GeometryDrawings
                GeometryDrawing whiteGeometryDrawing = new GeometryDrawing
                {
                    Brush = new SolidColorBrush(Color.FromArgb(0xFF, 0xFF, 0xFF, 0xFF)),
                    Geometry = Geometry.Parse(textGeometry)
                };
                GeometryDrawing blueGeometryDrawing = new GeometryDrawing
                {
                    Brush = new SolidColorBrush(Color.FromArgb(0xFF, 0x1B, 0x2C, 0x59)),
                    Geometry = Geometry.Parse(outLineGeometry)
                };

                // Add GeometryDrawings to nested DrawingGroup
                nestedDrawingGroup.Children.Add(whiteGeometryDrawing);
                nestedDrawingGroup.Children.Add(blueGeometryDrawing);

                // Add nested DrawingGroup to root DrawingGroup
                rootDrawingGroup.Children.Add(nestedDrawingGroup);

                // Create DrawingImage using root DrawingGroup
                DrawingImage drawingImage = new DrawingImage(rootDrawingGroup);

                // Add Dictionary
                if(!_mapNumToDrawingImage.ContainsKey(key))
                    _mapNumToDrawingImage.Add(key, drawingImage);
            }
            catch (Exception e)
            {
                Console.WriteLine(e);
            }
        }

        /// <summary>
        /// 获取数字对应的DrawingImage
        /// </summary>
        /// <returns></returns>
        public DrawingImage GetDrawingImage(int key)
        {
            try
            {
                if (_mapNumToDrawingImage.ContainsKey(key))
                    return _mapNumToDrawingImage[key];
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
            }
            return null;
        }

        /// <summary>
        /// 合成DrawingImage
        /// </summary>
        /// <returns></returns>
        public DrawingImage MergeDrawingImages(double spacing, List<DrawingImage> drawingImages)
        {
            try
            {
                DrawingGroup drawingGroup = new DrawingGroup();
                double offsetX = 0;
                for (int i = 0; i < drawingImages.Count; i++)
                {
                    DrawingImage drawingImage = drawingImages[i];
                    var drawingImageRect = drawingImage.Drawing.Bounds;
                    Drawing cloneDrawing = drawingImage.Drawing.Clone();
                    var translatedCloneDrawing = new TranslateTransform(offsetX, 0);
                    var transformedDrawing = new DrawingGroup();
                    transformedDrawing.Children.Add(cloneDrawing);
                    transformedDrawing.Transform = translatedCloneDrawing;
                    drawingGroup.Children.Add(transformedDrawing);
                    if (i < drawingImages.Count - 1)
                        offsetX += drawingImageRect.Width + spacing;
                }
                return new DrawingImage(drawingGroup);
            }
            catch (Exception e)
            {
                Console.WriteLine(e);
            }

            return null;
        }
    }
